import './App.css';
import Search from "./components/Search";
import OffreListList from './components/OffreList';
import { useState , useEffect } from 'react'
import CartList from './components/Reservation';
import axios from "axios"
import Create from './components/Create';

const App = () => {
const [menuView, setMenuView] = useState(false);
const [view,setView] = useState("productList");
const [data , setData] = useState([])
const [trigger,setTrrigger]=useState(false)
const [cart , setCart] = useState([])

console.log(data)
useEffect(() => {
  axios.get("http://localhost:3000/api/offer").then((res) => {
     
  
  setData(res.data);
    })
    .catch((error) => {
      console.log(error);
    });
}, [trigger]);

const addToCart = (offer) =>{
  const {name , price } = offer
  const cartItem ={name,price}
setCart([...cart,offer] )
}

const stalCart=(obj)=>{
setCart([...cart,obj])
}


/*const toggleMenu = ()=> {
  setMenuView(!menuView)
}
*/
const switchView = (x) => {
  setView(x)
}

  return (
  <div>
    
    <div class="navbar">
      <div class="logo">
        <h1>Travel</h1>
      </div>
      
    <div class="menu">
      <ul>
      <li><a href="#">Home</a></li>
      <li><a href="#">services</a></li>
      <li><a href="#">Places</a></li>
      <li><a href="#">Discount</a></li>
      <li><a href="#">Contact</a></li>
      <li><a href="#">Booking</a></li>
    </ul>
       
    </div>
    <div class="signup" onClick={()=>switchView ("cart")}>
  <a href="#">Reservation</a>      

    </div>
    <span className="items" onClick={()=>setView("add")}>
            ADD
          </span>
  </div>

          {view ==="productList" && <ProductsList trigger={setTrrigger}  data={data} cart={addToCart}/>}
          {view ==="cart"&&<CartList cartItem={cart}/>}   
          {view ==="create"&& <Create/>}
    </div>
  );
}

export default App;
