import axios from 'axios';
import React, { useState, useEffect } from 'react';
const data = require ("./../data.json")

const Reservation = ({ detailsReservation }) => {
  const [data, setData] = useState([]);

  axios.get("http://localhost:3000/api/offer").then((res)=>{
    setData(res.data)

  }).catch((error)=>{
console.log(error)
  })

  useEffect(() => {
    setData(detailsReservation);
  }, [detailsReservation]); 

  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {data.map((e, i) => (
        <div key={i} className='reservation-details'>
          <span>Country name: {e.name}</span>
          <span>Price: ${e.price}</span>
          <button className='reservation-details-button'>Remove</button>
          <br /><br />

          <a>First name</a>
          <br />
          <input type="text" />
          <br /><br />

          <a>Last name</a>
          <br />
          <input type="text" />
          <br /><br />

          <a>Phone Number</a>
          <br />
          <input type="tel" />
          <br />

          <h3>Total: ${e.price}</h3>
        </div>
      ))}
    </div>
  );
}

export default Reservation;
