import './App.css';
import axios from "axios"



function App(props) {
  //const [data, setData] = useState([])
  //const [Tunisia, setTunisia] = useState([])
  //const [Turkey, setTurkey] = useState([])
  //const [Spain, setSpain] = useState([])

/*useEffect(()=>{
  axios.get("http://localhost:1000/api/products").then((res)=>{
    setTunisia(res.data.filter((e)=> e.name ==="Tunisia"))
    setTurkey(res.data.filter((e)=>e.name ==="Turkey"))
    setSpain(res.data.filter((e)=>e.name==="Spain"))
  })
})*/



  return (
    <div className="body">
      {props.data && props.data.map((e, i) => (
        <div key={i} className="places">
          <h2>{e.name}</h2>

          <img
            src={e.imageUrl}
            style={{ width: '300px', height: '250px', borderRadius: '12px' }}
            alt={e.name}
          />
          <h4>{e.price}</h4>


          <br />
          <br />
          <a href="http://localhost:3001/#" onClick={() => props.cart(e)}>Book Now</a>
          <br />

          <br />
          <br />
          <a href="#">Update</a>
          <br />
          <br />
          <br />
          <a href="#">Delete </a>

          <div >
            <div>

            </div>

            <div class="menu">

            </div>
          </div>

        </div>
      ))}
    </div>
  );
}

export default App;
