import './App.css';
import Search from "./components/Search";
import ProductsList from './components/ProductsList';
import { useState , useEffect } from 'react'
import CartList from './components/CartList';
import axios from "axios"

const App = () => {
const [menuView, setMenuView] = useState(false);
const [view,setView] = useState("productList");
const [data , setData] = useState([])

const [trigger,setTrrigger]=useState(false)
const [cart , setCart] = useState([])

console.log(data)
useEffect(() => {
  axios.get("http://localhost:1000/api/products").then((res) => {
     
  
  setData(res.data);
    })
    .catch((error) => {
      console.log(error);
    });
}, [trigger]);

const addToCart = (product) =>{
setCart([...cart,product] )
}



const stalSearch = (input) => {
  const filtr= data.filter((e) => {
    return e.name.toLowerCase().includes(input.toLowerCase());
  })
  setData(filtr)
};


const toggleMenu = ()=> {
  setMenuView(!menuView)
}
const switchView = (x) => {
  setView(x)
}
  return (
   <div>
     <div className="body">
      <div className="heading">
        <br />
        <br />
      </div>
      <div className="tours">
        <div className="places">
          
          <br />
          <br />
          <a href="#">Book Now</a>
        </div>
        <div className="places">
          <h2>Turkey</h2>
          <img
            src="https://www.albawaba.net/sites/default/files/styles/d08_standard/public/im_new/noor.ahmad/%D8%B4%D8%A7%D8%B1%D8%B9%20%D8%A7%D9%84%D8%B9%D8%B1%D8%A8%20%D9%81%D9%8A%20%D8%A7%D8%B3%D8%B7%D9%86%D8%A8%D9%88%D9%84.jpg?h=c9f93661&itok=eFYlAdIM"
            style={{ width: '300px', height: '250px', borderRadius: '12px' }}
            alt="Turkey"
          />
          <br />
          <br />
          <a href="#">Book Now</a>
        </div>
        <div className="places">
          <h2>Spain</h2>
          <img
            src="https://imgproxy.natucate.com/wF8RACmH64YN4YV3BSgRXmhB7LtrW2eBW-xf6wRpJmQ/rs:fill/aHR0cHM6Ly93d3cubmF0dWNhdGUuY29tL21lZGlhL3BhZ2VzL3JlaXNlemllbGUvNDI4YTYzZWUtMmYzOS00YmFjLTgwY2UtNmY2N2Y4Yzc1NzJlL2MxNDJhZjc3MWUtMTY3OTQ4Njc1MC9zcGFuaWVuLWxhZW5kZXJpbmZvcm1hdGlvbmVuLXN0YWR0LW96ZWFuLXdhc3Nlci1uYXR1Y2F0ZS5qcGc"
            style={{ width: '300px', height: '250px', borderRadius: '12px' }}
            alt="Spain"
          />
          <br />
          <br />
          <a href="#">Book Now</a>
        </div>
      </div>
    </div>

          {view ==="productList" && <ProductsList  products={data} addToCart={addToCart}/>}
          {view ==="cart"&&<CartList cartItem={cart}/>}   
    </div>
  );
}

export default App;
