import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';

const Reservation = (props) => {
  const [data, setData] = useState([]);
  const [trigger, setTrigger] = useState(false);
  console.log(data, "this is our data");

  useEffect(() => {
    axios.get("http://localhost:3000/api/offer")
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {props.cart.map((e, i) => (
        <div key={i} className='reservation-details'>
          <span>Country Name : {e.name}</span>
          <span>Price: {e.price}</span>
          <h2> </h2>
                 
        </div>
      ))}

      <br />
      <br />


      <TextField
      id="outlined-basic"
      label="Full Name"
      variant="outlined"
      fullWidth
    />

    <br />
    <br />

    <TextField
      id="outlined-basic"
      label="Email"
      variant="outlined"
      fullWidth
      type="email"
    />

    <br />
    <br />

    <TextField
      id="outlined-basic"
      label="Phone Number"
      variant="outlined"
      fullWidth
      type="tel"
    />
  <br />
    <br />
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={age}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Select Your age" />}
      />
      <br />
      <br />
      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={country}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Select Your Country" />}
      />

  
    </div>

    
  );
}


const age = [
  { label: "18"},
  { label: "19" },
  { label: "20"},
  { label: "21" },
  { label: "22"},
  { label: "23" },
  { label: "24"},
  { label: "25" },
  { label: "26"},
  { label: "27" },
  { label: "28"},
  { label: "29" },
  
 ];

 const country = [
  { label: "Tunisia"},
  { label: "Algeria" },
  { label: "Egypt"},
  { label: "Libya" },
  { label: "morrocco"},
  { label: "spain" },
  { label: "united kingdom"},
  { label: "united state" },
  { label: "Turkey"},

 ]
 
 
 

export default Reservation;
