import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function OffreList(props) {
  const [trigger , setTrrigger] = useState(false)


  const Reservation = (props) => {
    const [data, setData] = useState([]);
    const [trigger,setTrigger] = useState(false)
    console.log(data, "this is our data");
  
    useEffect(() => {
      axios.get("http://localhost:3000/api/offer")
        .then((res) => {
          setData(res.data);
        })
        .catch((error) => {
          console.log(error);
        });
    }, []);
  
    return (
      <div className='reservation'>
        <h1>Reservation</h1>
        { props.data && 
          props.data.map((e, i) => (
          <div key={i} className='reservation-details'>
            <span>Country name: {e.name}</span>
            <span>Price: ${e.price}</span>
            <button className='reservation-details-button'>Remove</button>
            <br /><br />
  
           
          </div>
        ))}
      </div>
    );
  }
  



  const remove = (id) => {
    axios
      .delete(`http://localhost:3000/api/offer/${id}`)
      .then((result) => {
        console.log("hiiii");
        props.trigger(!props.trigger);
      })
      .catch((error) => {
        console.log(error);
      },[trigger]);
  };

  return (
    <div className="body">
      {props.data &&
        props.data.map((e, i) => (
          <div key={i} className="places">
            <h2>{e.name}</h2>
            <img
              src={e.imageUrl}
              style={{ width: '300px', height: '250px', borderRadius: '12px' }}
              alt={e.name}
            />
            <br />
            <br />
            <h4>{e.price}</h4>
            <br />
            <br />
            <a href="#"  onClick={()=>{}}>Book Now</a>
            <br />
            <br />
            <br />
            <a href="#" onClick={() => {
          props.offreUpdate(e)
        }}>Update</a>
            <br />
            <br />
            <br />
            <a
              href="#"
              onClick={() => {
                remove(e._id);
              }}
            >
              Delete
            </a>
            <div>
              <div></div>
              <div className="menu"></div>
            </div>
          </div>
        ))}
    </div>
  );
}

export default OffreList;
