import './App.css';
import Search from "./components/Search";
import ProductsList from './components/ProductsList';
import { useState , useEffect } from 'react'
import CartList from './components/CartList';
import axios from "axios"

const App = () => {
const [menuView, setMenuView] = useState(false);
const [view,setView] = useState("productList");
const [data , setData] = useState([])
const [trigger,setTrrigger]=useState(false)
const [cart , setCart] = useState([])

console.log(data)
useEffect(() => {
  axios.get("http://localhost:3000/api/offer").then((res) => {
     
  
  setData(res.data);
    })
    .catch((error) => {
      console.log(error);
    });
}, [trigger]);

const addToCart = (offer) =>{
  const {name , price } = offer
  const cartItem ={name,price}
setCart([...cart,offer] )
}




/*const toggleMenu = ()=> {
  setMenuView(!menuView)
}
*/
const switchView = (x) => {
  setView(x)
}

  return (
  <div>
    
    <div class="navbar">
      <div class="logo">
        <h1>Travel</h1>
      </div>
      
    <div class="menu">
      <ul>
      <li><a href="#">Home</a></li>
      <li><a href="#">services</a></li>
      <li><a href="#">Places</a></li>
      <li><a href="#">Discount</a></li>
      <li><a href="#">Contact</a></li>
      <li><a href="#">Booking</a></li>
    </ul>
       
    </div>
    <div class="signup" onClick={()=>switchView ("cart")}>
  <a href="#">Reservation</a>      

    </div>
    
  </div>

  <br />
          <br />
          <a href="http://localhost:3001/#">Book Now</a>
          <br />


          {view ==="productList" && <ProductsList  data={data} cart={addToCart}/>}
          {view ==="cart"&&<CartList cartItem={cart}/>}   
    </div>
  );
}

export default App;
