import React, { useState, useEffect } from 'react';

const Reservation = ({ detailsReservation }) => {
  const [data, setData] = useState([]);

  useEffect(() => {
    setData(detailsReservation);
  }, [detailsReservation]); // Corrected dependency array for useEffect

  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {data.map((e, i) => (
        <div key={i} className='reservation-details'>
          <span>Country name: {e.countryName}</span>
          <span>Price: ${e.price}</span>
          <button className='reservation-details-button'>Remove</button>
          <br /><br />

          <a>First name</a>
          <br />
          <input type="text" />
          <br /><br />

          <a>Last name</a>
          <br />
          <input type="text" />
          <br /><br />

          <a>Phone Number</a>
          <br />
          <input type="tel" />
          <br />

          <h3>Total: ${e.price}</h3>
        </div>
      ))}
    </div>
  );
}

export default Reservation;
