import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Reservation = (props) => {
  const [data, setData] = useState([]);
  const [Tunisia, setTunisia] = useState([])
  const [Turkey, setTurkey] = useState([])
  const [Spain, setSpain] = useState([])

useEffect(()=>{
  axios.get("http://localhost:1000/api/products").then((res)=>{
    setTunisia(res.data.filter((e)=> e.name ==="Tunisia"))
    setTurkey(res.data.filter((e)=>e.name ==="Turkey"))
    setSpain(res.data.filter((e)=>e.name==="Spain"))
  })
})


  console.log(data, "this is our data");

  useEffect(() => {
    axios.get("http://localhost:3000/api/offer")
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {data.map((e, i) => (
        <div key={i} className='reservation-details'>
          <span>Country name: {e.name}</span>
          <span>Price: ${e.price}</span>
          <button className='reservation-details-button'>Remove</button>
          <br /><br />

         
        </div>
      ))}
    </div>
  );
}

export default Reservation;
