import React from 'react';
import './App.css';
import axios from 'axios';
import { useEffect } from 'react';
function App(props) {
const remove = (id) =>{
useEffect(()=>{
  axios.delete(`http://localhost:3000/api/offers${id}`).then((result)=>{console.log(result)}).catch((error)=>{console.log(error)})
})
}
  return (
    <div className="body">
      {props.data && props.data.map((e, i) => (
        <div key={i} className="places">
          <h2>{e.name}</h2>
          <img
            src={e.imageUrl}
            style={{ width: '300px', height: '250px', borderRadius: '12px' }}
            alt={e.name}
          />
          <h4>{e.price}</h4>
          <br />
          <br />
          <a href="#">Book Now</a>
          <br />
          <br />
          <br />
          <a href="#">Update</a>
          <br />
          <br />
          <br />
          <a href="#" onClick={()=>{remove(e._id)
          props.trigger(!props.trigger)
          }}>Delete </a>
          <div >
            <div>
            </div>
            <div class="menu">
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}
export default App;
