import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import './App.css';
import OffreList from './components/OffreList';
import Reservation from './components/Reservation';
import axios from 'axios';

const App = () => {
  const [view, setView] = useState('offertList'); // Default view is 'offertList'

  useEffect(() => {
    axios.get('http://localhost:3000/api/offer')
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <Router>
      <div>
        {/* Your navigation header */}
        <Switch>
          <Route path="/" exact>
            {view === 'offertList' && (
              <OffreList
                trigger={setTrrigger}
                offreUpdate={updated}
                carts={stalCart}
                data={data}
                cart={cart}
                setView={setView} // Pass setView function to change view
              />
            )}
            {view === 'reservation' && <Reservation />}
          </Route>
          {/* Other routes */}
        </Switch>
      </div>
    </Router>
  );
}

export default App;
