    imageUrl:""
  })
  const handelChange = (e) =>{
    setcreated({[e.target.name]:e.target.value})
  }

  const handelClick = () =>{
    axios.post("http://localhost:3000/api/offer",created).then((result)=>{console.log(result)}).catch((error)=>{console.log(error)}) 

}
  
