import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Reservation = (props) => {
  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {props.cart.length > 0 ? (
        props.cart.map((offer, index) => (
          <div key={index} className='reservation-details'>
            <span>Country name: {offer.name}</span>
            <span>Price: ${offer.price}</span>
            <button
              className='reservation-details-button'
              onClick={() => props.removeCartItem(index)} // You'll need to implement this function in the App component
            >
              Remove
            </button>
            <br /><br />
          </div>
        ))
      ) : (
        <p>Your cart is empty.</p>
      )}
    </div>
  );
};

export default Reservation;
