import React from 'react';
import axios from 'axios';
import { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';

function Create() {
  const [created, setCreated] = useState({
    name: '',
    price: '',
    imageUrl: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setCreated((prevState) => ({
      ...prevState,
      [name]: value,
    }));
  };

  const handleClick = () => {
    axios
      .post('http://localhost:3000/api/offer', created)
      .then((result) => {
        console.log(result);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <Box
      component="form"
      sx={{
        '& .MuiTextField-root': { m: 1, width: '100%' },
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
      }}
      noValidate
      autoComplete="off"
    >
      <div>
        <TextField
          required
          id="outlined-required"
          label="Name"
          name="name"
          onChange={handleChange}
        />

        <TextField
          required
          id="outlined-required"
          label="Price"
          name="price"
          onChange={handleChange}
        />
        <TextField
          required
          id="outlined-required"
          label="Image URL"
          name="imageUrl"
          onChange={handleChange}
        />
      </div>
      <button
        onClick={handleClick}
        style={{
          marginTop: '20px',
          padding: '10px 20px',
          backgroundColor: '#007bff',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
          cursor: 'pointer',
        }}
      >
        Add
      </button>
    </Box>
  );
}

export default Create;
