import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Autocomplete from '@mui/material/Autocomplete';
import TextField from '@mui/material/TextField';

const Reservation = (props) => {
  const [data, setData] = useState([]);
  const [trigger, setTrigger] = useState(false);
  console.log(data, "this is our data");

  useEffect(() => {
    axios.get("http://localhost:3000/api/offer")
      .then((res) => {
        setData(res.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className='reservation'>
      <h1>Reservation</h1>
      {props.cart.map((e, i) => (
        <div key={i} className='reservation-details'>
          <span>Country Name : {e.name}</span>
          <span>Price: ${e.price}</span>
          <h2> </h2>
          <button className='reservation-details-button'>Remove</button>
          <br /><br />
        </div>
      ))}

      <Autocomplete
        disablePortal
        id="combo-box-demo"
        options={top100Films}
        sx={{ width: 300 }}
        renderInput={(params) => <TextField {...params} label="Choose your bed (subject to availability)" />}
      />
    </div>
  );
}

// Top 100 films as rated by IMDb users. http://www.imdb.com/chart/top
const bed = [
  { label: 'The Shawshank Redemption', year: 1994 },
  { label: 'The Godfather', year: 1972 },
  { label: 'The Godfather: Part II', year: 1974 },
  { label: 'The Dark Knight', year: 2008 },
  { label: '12 Angry Men', year: 1957 },
  { label: "Schindler's List", year: 1993 },
  { label: 'Pulp Fiction', year: 1994 },
  {
    label: 'The Lord of the Rings: The Return of the King',
    year: 2003,
  },
  { label: 'The Good, the Bad and the Ugly', year: 1966 },
  { label: 'Fight Club', year: 1999 },
  {
    label: 'The Lord of the Rings: The Fellowship of the Ring',
    year: 2001,
  },];

export default Reservation;
